mapper.select:
select *, clazz.name clazz_name
from student left join clazz on student.clazz_id = clazz.id

    student.name like concat('%', #{name}, '%')
    and degree = #{degree}
    and clazz_id = #{clazzId}

order by student.update_time desc

mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, degree = #{degree}, graduation_date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, clazz_id = #{clazzId},
where id = #{id}
